        python $SONIC_CLI_ROOT/sonic-cli-show-ospfv2.py show_ip_ospf_neighbor show_ip_ospf_neighbor.j2 ${vrf-name} 

    else

        if test "${neighborshowtype}" = "neighip"; then

            python $SONIC_CLI_ROOT/sonic-cli-show-ospfv2.py show_ip_ospf_neighbor_detail show_ip_ospf_neighbor_detail.j2 ${vrf-name} ${neighip} 

        elif test "${neighborshowtype}" = "interfacename" && test "${interfacename}" = "detail"; then

            python $SONIC_CLI_ROOT/sonic-cli-show-ospfv2.py show_ip_ospf_neighbor_detail show_ip_ospf_neighbor_detail.j2 ${vrf-name} 

        elif test "${neighborshowtype}" = "interfacename" && test "${interfacename}" != "detail" && test "${detail}" != ""; then

            python $SONIC_CLI_ROOT/sonic-cli-show-ospfv2.py show_ip_ospf_neighbor_detail show_ip_ospf_neighbor_detail.j2 ${vrf-name} ${interfacename} 

        elif test "${neighborshowtype}" = "interfacename" && test "${interfacename}" != "detail"; then

            python $SONIC_CLI_ROOT/sonic-cli-show-ospfv2.py show_ip_ospf_neighbor show_ip_ospf_neighbor.j2 ${vrf-name} ${interfacename} 

        fi

    fi

elif test "${cmdtype}" = "interface" && test "${traffic}" = "" && test "${interfacename}" = ""; then

    python $SONIC_CLI_ROOT/sonic-cli-show-ospfv2.py show_ip_ospf_interface show_ip_ospf_interface.j2 ${vrf-name} 

elif test "${cmdtype}" = "interface" && test "${traffic}" = "" && test "${interfacename}" != ""; then

    python $SONIC_CLI_ROOT/sonic-cli-show-ospfv2.py show_ip_ospf_interface show_ip_ospf_interface.j2 ${vrf-name} ${interfacename} 

elif test "${cmdtype}" = "interface" && test "${traffic}" != "" && test "${interfacename}" = ""; then

    python $SONIC_CLI_ROOT/sonic-cli-show-ospfv2.py show_ip_ospf_interface_traffic show_ip_ospf_interface_traffic.j2 ${vrf-name} 

elif test "${cmdtype}" = "interface" && test "${traffic}" != "" && test "${interfacename}" != ""; then

    python $SONIC_CLI_ROOT/sonic-cli-show-ospfv2.py show_ip_ospf_interface_traffic show_ip_ospf_interface_traffic.j2 ${vrf-name} ${interfacename} 

elif test "${cmdtype}" = "route"; then

    python $SONIC_CLI_ROOT/sonic-cli-show-ospfv2.py show_ip_ospf_route show_ip_ospf_route.j2 ${vrf-name} 

elif test "${cmdtype}" = "border-routers"; then

    python $SONIC_CLI_ROOT/sonic-cli-show-ospfv2.py show_ip_ospf_border_routers show_ip_ospf_border_routers.j2 ${vrf-name} 

fi
